import Fetch from 'isomorphic-unfetch';
import React, { Component } from 'react'
import Layout from '../components/layout'
import Comments from '../components/commonts'
import Link from 'next/link';

class Index extends Component {
    constructor(props) {
        super(props)

        this.state = {
            posts : [],
            editOne: null,
            updatedPost : null  
        }
    }
    componentDidMount() {
        let {posts} = this.props;
        this.setState({posts});
    }    

    static async getInitialProps(){
        const res = await fetch('http://jsonplaceholder.typicode.com/posts');
        const data = await res.json();
        return { posts: data }
    }

    

    updatePost(post) {
        let {editOne} = this.state;
        this.setState({editOne:post.id});
    }

    handleChange = (e, post) => {
        post.body = e.target.value;
        this.setState({updatedPost: post});
    }

    submitPost = () => {  
        this.setState({  editOne: null })
     } 

    render() {
        let {editOne, posts, updatedPost} = this.state;
        console.log('uuuuuu',updatedPost)

        return (
            <Layout>
                <div style={  {position : "absolute",
            left:  "150px",
            width: "300px",
            height: "120px"}   }>
                   {
                        posts.map((post, i) => (
                            <div  key={i} >
                            <h3 >{post.title} </h3>
                            <p >{post.body}</p>
                        {(editOne === post.id) && <textarea onChange={(e) => this.handleChange(e, post)}>{post.body}</textarea>}
                            <button onClick={() => this.updatePost(post)}>update</button>
                        {(editOne === post.id) && <button onClick={this.submitPost }>submit</button> }
                         </div>
                        ))
                    }
                    <Modal.Dialog>
                        <Modal.Header closeButton>
                            <Modal.Title>Modal title</Modal.Title>
                        </Modal.Header>

                        <Modal.Body>
                            <p>Modal body text goes here.</p>
                        </Modal.Body>

                        <Modal.Footer>
                            <Button variant="secondary">Close</Button>
                            <Button variant="primary">Save changes</Button>
                        </Modal.Footer>
                </Modal.Dialog>
                  </div>  
        </Layout>
        )
    }
}

export default Index
