import React, { Component } from 'react'
import Layout from '../components/layout'
import index from './index'

class Users extends Component {
    constructor(props) {
      super(props);
      this.state = {
        userName: '',
        postBody: ''
      };
    }
  
    
  
     onchange (e){
      event.preventDefault();
      this.setState({ [e.target.name] : e.target.value })
    }

    onclick (event){
      event.preventDefault();
      console.log(this.state.postBody)
      console.log(this.state.userName)



    }
  
    deleteItem(idd) {
    //   console.log(idd)
    //    let user = this.state.;
    //    
    //    this.setState({ user: user })
      // console.log(this.state.users[i]._id)    onChange={this.props.onchange.bind(this)}
       
      }

      static async getInitialProps(){
        const res = await fetch('http://jsonplaceholder.typicode.com/users');
        const data = await res.json();
        
        return { users: data }
    
    
    }
      
   
    render() {
      const { fullname } = this.state.userName
      return (
      <Layout>
        <div  >
                   {
                        this.props.posts.map((user, i) => (
                            <div  key={i} >
                            <h3 >{user.name} </h3>
                            </div>
                        ))
                    }
                  </div>  

        <form style={  {position : "absolute",
            left:  "150px",
            width: "300px",
            height: "120px"}   }>
          <textarea name="postBody"  placeholder="post.."  onChange={this.props.onchange} ></textarea><br></br>
          <label>user name</label><input  type="text" name="userName" onChange={this.props.onchange} />
          <input type="submit" value="post" onClick={ this.props.onclick}/>
       </form>
      </Layout>
      
      );
    }
  }
  
  
  export default Users
