import Fetch from 'isomorphic-unfetch';
import React, { Component } from 'react'
import Layout from '../components/layout'
import Comments from '../components/commonts'
import Link from 'next/link';

class Index extends Component {
    constructor(props) {
        super(props)

        this.state = {
            posts : [],
            editOne: null
            
        }
    }
    componentDidMount() {
        let {posts} = this.props;
        this.setState({posts});
    }    

    static async getInitialProps(){
        const res = await fetch('http://jsonplaceholder.typicode.com/posts');
        const data = await res.json();
        return { posts: data }
    }

    updatePost(post) {
        let {editOne} = this.state;
        this.setState({editOne:post.id});
    }

    handleChange = (e) => {
        let newPost;
        newPost = e.target.value;
        console.log(newPost)
    }
    render() {
        //console.log(this.props.posts)
        let {editOne, posts} = this.state;
        console.log('eeeeeeeee',editOne)
        return (
            <Layout>
                <div style={  {position : "absolute",
            left:  "150px",
            width: "300px",
            height: "120px"}   }>
                   {
                        posts.map((post, i) => (
                            <div  key={i} >
                            <h3 >{post.title} </h3>
                            <p >{post.body}</p>
                        {(editOne === post.id) && <textarea onChange={this.handleChange}>{post.body}</textarea>}
                            <button onClick={() => this.updatePost(post)}>update</button>
                         </div>
                        ))
                    }
                  </div>  
        </Layout>
        )
    }
}

export default Index
