import React, { Component } from 'react'
import Layout from '../components/layout'

const neededComments = []

class Commonts extends Component {
    constructor(props) {
        super(props)

        this.state = {
            comments: []
        }
    }

    
    componentDidMount() {
        let {comments} = this.props;
        this.setState({comments: comments});
        
    }  

    takefixedComments = () => {
        // for (let i=0; i<50; i++) {
        //     let { commonts } = this.state
        //     neededComments.push(<li key={i}>{commonts[i]}</li>)
        //     }

        
    }

    static async getInitialProps(){
        const res = await fetch('http://jsonplaceholder.typicode.com/comments');
        const data = await res.json();
        console.log(data)
        return { comments: data }
    
    }

    render() {
        console.log(this.state.comments)
        return (
            <Layout>
                <ul>
                 {
                    this.state.comments.length > 0 &&  this.state.comments.map((comment, i) => (
                        <li key={i}> { comment.body } </li>
                     ))
                 }
                </ul>
            </Layout>
        )
    }
}

export default Commonts
