import Fetch from 'isomorphic-unfetch';
import React, { Component } from 'react'
import Layout from '../components/layout'
import Comments from './comments'
import Link from 'next/link';
import Popup from "reactjs-popup"

class Index extends Component {
    constructor(props) {
        super(props)

        this.state = {
            posts : [],
            editOne: null,
            updatedPost : null,
            showCreatePostForm: false
        }
    }
    componentDidMount() {
        let {posts} = this.props;
        this.setState({posts});
    }    

    static async getInitialProps(){
        const res = await fetch('http://jsonplaceholder.typicode.com/posts');
        const data = await res.json();
        return { posts: data }
    }

    

    updatePost(post) {
        let {editOne } = this.state;
        this.setState({editOne : post.id});
    }

    handleChange = (e, post) => {
        post.body = e.target.value;
        this.setState({updatedPost: post});
    }

    submitPost = () => {  
        this.setState({  editOne: null })
     } 

     deletePost = (e, post, i) => {
         const { posts } = this.state
         console.log(post.id)
         posts.splice(i, 1);
         this.setState({ posts: posts })
     } 

    render() {
        let {editOne, posts, updatedPost} = this.state;
        console.log('uuuuuu',updatedPost)

        return (
            <Layout>
            <div>   
            <button > create new post </button>
            <form>
                <label>Describtion<span className="required">*</span></label>
                <label>title</label><input  type="text" name="title"  />
                <textarea name="body" ></textarea>
                <input type="submit" value="Submit" />
            </form>
            </div>
                <div style={  {position : "absolute",
            width: "300px",
            height: "120px"}   }>
                   {
                        posts.map((post, i) => (
                            <div  key={i} >
                            <h3 >{post.title} </h3>
                            <p >{post.body}</p>
                        
                            
            <Popup trigger={<button onClick={() => this.updatePost(post)}>update</button>}
                        position="right center">
                        <div>
                            <textarea onChange={(e) => this.handleChange(e, post)}>{post.body}</textarea>
                        </div>
                         <button  onClick={this.submitPost }>save</button>
            </Popup>
            <button onClick={ (e) => this.deletePost(e, post, i) }> delete </button>
                         </div>
                        ))
                    }
                  </div>  
        </Layout>
        )
    }
}

export default Index
