import Layout from './layout'
import React, { Component } from 'react'
import Popup from "reactjs-popup"
import { connect } from "react-redux";
// import PropTypes from 'prop-types'; 
import   { fetchPosts }  from "../redux/actions/postActions";
import  Userform  from "./userForm";

import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import AddIcon from '@material-ui/icons/Add';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import Fab from '@material-ui/core/Fab';
import { Grid } from '@material-ui/core';
import Box from '@material-ui/core/Box';




class Posts extends Component {
    constructor(props) {
        super(props)
        this.state = {
            last_edited_post: null,
            open: false, 
            setOpen: false,
        }

    }


    componentDidMount() {
        this.props.fetchPosts();
        console.log(this.props.posts)
       
        //  this.setState({ users: this.props.users })
    }   
    
    componentWillReceiveProps(nextProps){
        // if(nextProps.newUser){
        //     console.log(nextProps.newUser)
        //     this.props.users.unshift(nextProps.newUser)
        // }
    }


    // handleChange = (e, user) => {
    //     user.username = e.target.value
    //    this.setState({ last_edited_user: user })
    //    console.log(this.state.last_edited_user)

    // }
    // deleteUser = (e, user, i) => {
    //     console.log(i)
    //     console.log(this.props.users)
    //     this.props.users.splice(i, 1)
    //     console.log(this.props.users)
    //     this.setState({users: this.props.users })
    //     console.log(this.state.users)

    
    // } 

    
     handleClose = () => {
        this.setState({ open: false }) 
      };

      updateUser = (e, post) => {
        this.setState({ open: true })
        // console.log(user.id)
        // this.setState({ currentUserName: user.username })
        // console.log(user)
        // this.props.users.push(user)
        // this.setState({users: this.props.users })
      }

    render() {
        return(
            
                
        <Grid container style={{ marginTop: 50}}>
            <Grid item sm ={ 8   }>
                
            {
                this.props.posts.map((post, i) => (
                   
                    <Box  key={i} component="span" display="block" p={1} m={1} bgcolor="#e0e0e0">
                    <Box  p={1} m={1}> {post.title} </Box>
                    <Box p={1} m={1}> {post.body}</Box>
                    
                <Fab color="primary" aria-label="edit" onClick={(e) => this.updateUser(e, post)}>
                    <EditIcon />
                </Fab>
                <Dialog open={this.state.open} onClose={this.handleClose} aria-labelledby="form-dialog-title">
                    <DialogTitle id="form-dialog-title">change post body </DialogTitle>
                    <div >
                    <DialogContent>
                    {/* <textarea   style={{ width: '200px' }} value = { post.body}></textarea> */}
                        <TextField 
                            autoFocus
                            margin="dense"
                            name="post body"
                            label="post body"
                            type="text ares"
                         
                           
                        />

                        
                    </DialogContent>
                    </div>
                    <DialogActions>
                    <Button onClick={this.handleClose} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={ this.handleClose} color="primary">
                        save
                    </Button>
                    </DialogActions>
                </Dialog>
                <Button
                    variant="contained"
                    color="secondary"
                    startIcon={<DeleteIcon />}
                    // onClick = { (e)=> this.deleteUser(e, user, i) }
                    >
                    Delete
                    </Button>
                </Box>    
                            ))
                        } 
                    </Grid> 
                     {/* <Grid item sm bgcolor="e0e0e0">
                        <Box   component="span" display="block" p={1} m={1} bgcolor="#e0e0e0" height= '98%'>
                        <Userform   />
                        </Box>
                     </Grid> */}
                </Grid>
                                
             
            
        )
    }
}

// Users.propTypes = {
//     fetchUsers: PropTypes.func.isRequired,
//     users: PropTypes.array.isRequired,
//     newUser: PropTypes.object 
// }

const mapStateToProps = (state) => ({
    posts: state.posts.posts
    // newUser: state.users.user
})

export default connect(mapStateToProps, {fetchPosts})(Posts)
