import React, { Component } from 'react'
import Layout from '../components/layout'
import Popup from "reactjs-popup"


class Commonts extends Component {
    constructor(props) {
        super(props)

        this.state = {
            comments: []
        }
    }

    
    componentDidMount() {
        let {comments} = this.props;
        this.setState({comments: comments});
        
    }  

    takefixedComments = () => {
        // for (let i=0; i<50; i++) {
        //     let { commonts } = this.state
        //     neededComments.push(<li key={i}>{commonts[i]}</li>)
        //     }

        
    }

    static async getInitialProps(){
        const res = await fetch('http://jsonplaceholder.typicode.com/comments');
        const data = await res.json();
        console.log(data)
        return { comments: data }
    
    }

    render() {
        console.log(this.state.comments)
        return (
            <Layout>
                
                <div style={  {position : "absolute", width: "300px",height: "120px"}   }>
                  { 
                    this.state.comments.length > 0 &&  this.state.comments.map((comment, i) => (
                        <div key={i}> { comment.body } <div/>
                    <Popup trigger={<button onClick={()=>{ }}>update</button>}
                        position="right center">
                        <div>
                            <textarea style={  {padding: '40px'}  }>{comment.body}</textarea>
                        </div>
                         <button  onClick={this.submitPost }>save</button>
                   </Popup>
                   <button> delete </button>
                     </div>
                     ))
                 }
                </div>
                
            </Layout>
        )
    }
}

export default Commonts
