    constructor(props) {
        super(props)

        this.state = {
            posts : [],
            editOne: null
            
        }
    }
    componentDidMount() {
        let {posts} = this.props;
        console.log('ppp',posts) 
    }    

    static async getInitialProps(){
        const res = await fetch('http://jsonplaceholder.typicode.com/posts');
        const data = await res.json();
        return { posts: data }
    }

    updatePost(post) {
        console.log(post)
    }

    
    render() {
        //console.log(this.props.posts)
        let {editOne} = this.state
        return (
            <Layout>
                <div style={  {position : "absolute",
            left:  "150px",
            width: "300px",
            height: "120px"}   }>
                   {
                        this.props.posts.map((post, i) => (
                            <div  key={i} >
                            <h3 >{post.title} </h3>
                            {!editOne && <p >{post.body}</p>}
                            {editOne}
                            <Link href={`/update/${post.id}`}><a>update</a></Link>
                            </div>
                        ))
                    }
                  </div>  
        </Layout>
        )
    }
}

export default Index
