    constructor(props) {
        super(props)

        this.state = {
            posts : []
            
        }
    }
    
    static async getInitialProps(){
        const res = await fetch('http://jsonplaceholder.typicode.com/posts');
        const data = await res.json();
        console.log('dddddddddddd',data);

        return { posts: data }
    }

