        console.log(post)
    }

    
    render() {
        //console.log(this.props.posts)
        return (
            <Layout>
                <div style={  {position : "absolute",
            left:  "150px",
            width: "300px",
            height: "120px"}   }>
                   {
                        this.props.posts.map((post, i) => (
                            <div  key={i} >
                            <h3 >{post.title} </h3>
                            <p >{post.body} </p>
                            <Link href={`/update/${post.id}`}><a>update</a></Link>
                            </div>
                        ))
                    }
                  </div>  
        </Layout>
        )
    }
}

export default Index
