    //     const { users } = this.state
    //     console.log(user.id)
    //     users.splice(i, 1);
    //     this.setState({ users: users })
    // } 

    // handleCreatUser = (e) => {
    //     //const { newUser } = this.state
    //     this.setState({ [e.target.name] : e.target.value })
        
    //     const newUser = {
    //         id: this.state.id,
    //         name: this.state.name,
    //         username: this.state.username
    //     }

    //     this.setState({ newUser : newUser })
    // }

    // buCreateUser = (event) => {
    //     event.preventDefault();
    //     const { newUser, users } = this.state
    //     users.push(newUser)
    //     this.setState({ users })
    //     console.log(newUser)
    //  }

     handleClickOpen = () => {
         this.setState({ open: true })
      };
    
     handleClose = () => {
        this.setState({ open: false }) 
      };

    render() {
        return(
            
                <div> 
                <Userform />
                    {
                        this.props.users.map((user, i) => (
                            <div  key={i} >
                            <h3 >Name : {user.name} </h3>
                            <h4 >User Name : {user.username}</h4>
                            {/* <textarea onChange={(e) =>  this.handleChange(e, user) } value = { user.username}></textarea> */}
                           
      <Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
        Open form dialog
      </Button>
      <Dialog open={this.state.open} onClose={this.handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">user name </DialogTitle>
          <div  >
        <DialogContent>
          <DialogContentText>
            change user name
          </DialogContentText>
             <textarea onChange={(e) =>  this.handleChange(e, user) } value = { e.h4.user.username}></textarea>

        </DialogContent>
          </div>
        <DialogActions>
          <Button onClick={this.handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={this.handleClose} color="primary">
            save
          </Button>
        </DialogActions>
      </Dialog>
                <button > delete user</button>
                </div>    
                ))
            }  
            </div>
                    
                      
             
            
        )
    }
}

Users.PropTypes = {
    fetchUsers: PropTypes.func.isRequired,
    users: PropTypes.array.isRequired,
    newUser: PropTypes.object 
}

const mapStateToProps = (state) => ({
    users: state.users.users,
    newUser: state.users.user
})

export default connect(mapStateToProps, {fetchUsers})(Users)
