    constructor(props) {
        super(props)

        this.state = {
            posts : this.props 
            
        }
    }
    
    static async getInitialProps(){
        const res = await fetch('http://jsonplaceholder.typicode.com/posts');
        const data = await res.json();
        
        return { posts: data }
    
    
        const divStyle = {
           
        }
    }


    
    render() {
        //console.log(this.props.posts)
        return (
            <Layout>
                <div style={  {position : "absolute",
            left:  "150px",
            width: "300px",
            height: "120px"}   }>
                   {
                        this.props.posts.map((post, i) => (
                            <div  key={i} >
                            <h3 >{post.title} </h3>
                            <p >{post.body} </p>
                            </div>
                        ))
                    }
                  </div> 

            <form style = {{position : "absolute", right:  "150px", width: "300px",height: "120px"} }>
                <label>Post</label><input  type="text" name="postbody" placeholder="add pst"  />
                <textarea name="describtion" id="field5" className="field-long field-textarea" ></textarea>
                <input type="submit" value="Submit" onClick={ this.props.onclick.bind(this)}/>
            </form>      
        </Layout>
        )
    }
}

export default Index
